import React from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom'

import './styles.css';

const MovieItem = ({ id, imgURL, title, voteAvg, overview, releaseDate }) => {
  return (
    <Link path="/movie/{id}" id={id} className="itemWrapper">
      <div className="movieImgWrapper">
        <img src={`http://image.tmdb.org/t/p/w185/${imgURL}`} alt={`${title} Movie`}/>
      </div>
      <h3 className="title">{title}</h3>
      <div className="ratingWrapper">
        <h5 className="rating">{voteAvg}/10</h5>
      </div>
    </Link>
  );
};

