import React from 'react';
import PropTypes from 'prop-types';

import './styles.css';

const Pagination = ({ onPgInputFocus, onPgNumValueChange, pgInputValue, isNextBtnDisabled, isPrevBtnDisabled }) => {
  return (
    <div className="pagination">
      <button
        disabled={isPrevBtnDisabled}
        className="leftPgBtn movieInput"
        onClick={() => onPgNumValueChange('-')}
      >
      &lt;
      </button>
      <input
        className="movieInput pgInput"
        type="number"
        value={pgInputValue}
        onChange={(e) => onPgNumValueChange('input', e)}
        onFocus={(e) => onPgInputFocus(e)}
      />
      <button
        disabled={isNextBtnDisabled}
        className="rightPgBtn movieInput"
        onClick={() => onPgNumValueChange('+')}
      >
      &gt;
      </button>
    </div>
  );
};

Pagination.propTypes = {
  onPgInputFocus: PropTypes.func.isRequired,
  onPgNumValueChange: PropTypes.func.isRequired,
  pgInputValue: PropTypes.number.isRequired,
  isNextBtnDisabled: PropTypes.bool.isRequired,
  isPrevBtnDisabled: PropTypes.bool.isRequired,
};

Pagination.defaultProps = {

};

export default Pagination;
