import React from 'react'
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import PropTypes from 'prop-types';

import './styles.css';
import { getMovieData } from './actions';
import MovieGrid from '../../components/MovieGrid/index';


class HomePage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      pageTitle: 'Popular Movies',
      pgNum: 1,
      isSearchQuery: false,
      isPrevBtnDisabled: false,
      isNextBtnDisabled: false,
    };

    this.onSearchInputChange = this.onSearchInputChange.bind(this);
    this.onPgNumValueChange = this.onPgNumValueChange.bind(this);
  }

  componentDidMount() {
    this.props.getMovieData(this.state.pgNum);
  }

  onSearchInputChange(e) {
    // replace all spaces with %20 for the api url query
    const apiSearchQuery = e.target.value.replace(/ /gi, '%20');
    const pageTitleStr = e.target.value !== '' ? e.target.value : 'Popular Movies';
    const isSearchQueryAPI = e.target.value !== '' ? true : false;

    this.props.getMovieData(this.state.pgNum, apiSearchQuery);
    this.setState({ pageTitle: pageTitleStr, isSearchQueryAPI });

  }

  async onPgNumValueChange(pgOperation, e) {
    const apiSearchQuery = this.state.isSearchQueryAPI ? this.state.pageTitle.replace(/ /gi, '%20') : undefined;

    switch (pgOperation) {
      case 'input':
        await this.setState({ pgNum:  parseInt(e.target.value)});
        break;
      case '+':
        await this.setState({ pgNum: parseInt(this.state.pgNum + 1) });
        break;
      case '-':
        const newPgNum = parseInt(this.state.pgNum - 1);
        const isDisabled = newPgNum > 0;

        await this.setState({ pgNum: newPgNum, isPrevBtnDisabled: isDisabled });
        break;
      default:
      // if all fails, just make it first page
        await this.setState({ pgNum: 1 });
    }

    this.props.getMovieData(this.state.pgNum, apiSearchQuery);
  }

  render() {
    console.log('data: ', this.props.movieData)
    return(
      <div className="containerWrapper">
        <div className="searchbarRow">
          <input className="movieSearchbar movieInput" onChange={this.onSearchInputChange} placeholder="Enter a title of a movie" />
        </div>
        <MovieGrid movies={this.props.movieData} pgTitle={this.state.pageTitle} />
        <div className="pagination">
          <button className="leftPgBtn movieInput" onClick={() => this.onPgNumValueChange('-')}>&lt;</button>
          <input className="movieInput pgInput" type="number" value={this.state.pgNum} onChange={(e) => this.onPgNumValueChange('input', e)} />
          <button className="rightPgBtn movieInput" onClick={() => this.onPgNumValueChange('+')}>&gt;</button>
        </div>
      </div>
    );
  }
}

HomePage.propTypes = {
  getMovieData: PropTypes.func.isRequired,
};

HomePage.defaultProps = {

};

function mapStateToProps(state) {
  return {
    movieData: state.HomePage.movieData,
  };
}

function mapDispatchToProps(dispatch) {
  return bindActionCreators({ getMovieData }, dispatch);
}

export default connect(mapStateToProps, mapDispatchToProps)(HomePage);
