import { takeLatest, put } from 'redux-saga/effects';

import { FETCH_MOVIE_DATA } from './constants';
import {
  onMovieLoadDone,
  onMovieLoadFailed,
  onMovieLoadSuccess,
  onMovieLoadStart,
} from './actions';


  function* getMovies(action) {
    const API_KEY = '3d1fe54bcb2a662f3f0afd04c484ffdc';
    const SEARCH_QUERY = action.searchQueryString;
    const PG_NUM = action.pgNum;
    console.log('saga', SEARCH_QUERY);
		const requestPopularMovieURL = `https://api.themoviedb.org/3/movie/popular?api_key=${API_KEY}&language=en-US&page=1`;
    const requestSearchedMovieURL = `https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&language=en-US&query=${SEARCH_QUERY}&page=${PG_NUM}&include_adult=false`
    const apiURL = action.searchQueryString ? requestSearchedMovieURL : requestPopularMovieURL;

    try {
	    yield put(onMovieLoadStart());
	    const response = yield fetch(apiURL);
			const jsonData = yield response.json();

      yield put(onMovieLoadSuccess(jsonData));
      yield put(onMovieLoadDone());

	  } catch (e) {
      console.log('Error Occured', e);
      alert(`Error Occured! Status Code ${e.status_code}: ${e.status_message}`);
	    yield put(onMovieLoadFailed());
	  }
	}

	export function* HomePageSaga() {
    yield takeLatest(FETCH_MOVIE_DATA, getMovies);
	}

	export default HomePageSaga;
