import { takeLatest, put } from 'redux-saga/effects';

import { FETCH_MOVIE_DATA } from './constants';
import {
  onMovieLoadDone,
  onMovieLoadFailed,
  onMovieLoadSuccess,
  onMovieLoadStart,
} from './actions';


  function* getMovies(action) {
    //const SEARCH_QUERY = action.searchQueryString;
    // const PG_NUM = action.pgNum;

    try {
	    yield put(onMovieLoadStart());
	    const response = yield fetch(`/api/getPopularMovies/${action.pgNum}`);
			const jsonData = yield response.json();

      yield put(onMovieLoadSuccess(jsonData));
      yield put(onMovieLoadDone());

	  } catch (e) {
      console.log('Error Occured', e);
      alert(`Error Occured! Status Code ${e.status_code}: ${e.status_message}`);
	    yield put(onMovieLoadFailed());
	  }
	}

	export function* HomePageSaga() {
    yield takeLatest(FETCH_MOVIE_DATA, getMovies);
	}

	export default HomePageSaga;
