import React from 'react';
import PropTypes from 'prop-types';

import './styles.css';

const Pagination = ({onPgNumValueChange, pgValue, pgMaxValue, isNextBtnDisabled, isPrevBtnDisabled }) => {
  return (
    <div className="pagination">
      <button
        disabled={isPrevBtnDisabled}
        className="leftPgBtn movieInput"
        onClick={() => onPgNumValueChange('-')}
      >
      &lt;
      </button>
      <div className="movieInput pgInput">{pgValue}/{pgMaxValue}</div>
      <button
        disabled={isNextBtnDisabled}
        className="rightPgBtn movieInput"
        onClick={() => onPgNumValueChange('+')}
      >
      &gt;
      </button>
    </div>
  );
};

Pagination.propTypes = {
  onPgNumValueChange: PropTypes.func.isRequired,
  pgValue: PropTypes.number.isRequired,
  pgMaxValue: PropTypes.number.isRequired,
  isNextBtnDisabled: PropTypes.bool.isRequired,
  isPrevBtnDisabled: PropTypes.bool.isRequired,
};

Pagination.defaultProps = {

};

export default Pagination;
