import React from 'react'
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import PropTypes from 'prop-types';
// import { Link } from 'react-router-dom'

import './styles.css';
import { getMovieData } from './actions';


class HomePage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searchbarString: '',
    };

    this.onSearchInputChange = this.onSearchInputChange.bind(this);
  }

  componentDidMount() {
    const isSearchRequest = this.state.searchbarString !== '' ? true : false;
    const replaceSpace = this.state.searchbarString.replace(" ", "%20");

    this.props.getMovieData(isSearchRequest, replaceSpace);
  }

  onSearchInputChange(e) {
    console.log(e.target.value);
  }

  render() {
    console.log('MovieData: ', this.props.movieData);
    return(
      <div className="containerWrapper">
        <div className="searchbarRow">
          <input className="movieSearchbar" onChange={this.onSearchInputChange} placeholder="Enter a title of a movie" />
        </div>
        <div className="movieContainer">
          <div className="movieHeader">
            <h2>Popular Movies:</h2>
            <p>123 Results</p>
          </div>
          <div className="movieGrid">
            movies go here
          </div>
        </div>
      </div>
    );
  }
}

HomePage.propTypes = {
  getMovieData: PropTypes.func.isRequired,
};

HomePage.defaultProps = {

};

function mapStateToProps(state) {
  return {
    movieData: state.HomePage.movieData,
  };
}

function mapDispatchToProps(dispatch) {
  return bindActionCreators({ getMovieData }, dispatch);
}

export default connect(mapStateToProps, mapDispatchToProps)(HomePage);
