import React from 'react'
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import PropTypes from 'prop-types';

import './styles.css';
import { getMovieData } from './actions';
import MovieGrid from '../../components/MovieGrid/index';


class HomePage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      pageTitle: 'Popular Movies',
    };

    this.onSearchInputChange = this.onSearchInputChange.bind(this);
  }

  componentDidMount() {
    this.props.getMovieData(1);
  }

  onSearchInputChange(e) {
    // replace all spaces with %20 for the api url query
    const apiSearchQuery = e.target.value.replace(/ /gi, '%20');
    const pageTitleStr = e.target.value !== '' ? e.target.value : 'Popular Movies';

    this.props.getMovieData(apiSearchQuery);
    this.setState({ pageTitle: pageTitleStr });
  }

  render() {
    return(
      <div className="containerWrapper">
        <div className="searchbarRow">
          <input className="movieSearchbar" onChange={this.onSearchInputChange} placeholder="Enter a title of a movie" />
        </div>
        <MovieGrid movies={this.props.movieData} pgTitle={this.state.pageTitle} />
      </div>
    );
  }
}

HomePage.propTypes = {
  getMovieData: PropTypes.func.isRequired,
};

HomePage.defaultProps = {

};

function mapStateToProps(state) {
  return {
    movieData: state.HomePage.movieData,
  };
}

function mapDispatchToProps(dispatch) {
  return bindActionCreators({ getMovieData }, dispatch);
}

export default connect(mapStateToProps, mapDispatchToProps)(HomePage);
